mapper.insert:
INSERT INTO EMPL_QST(   QA_NOTIWR_NO
					  , TTL
					  , ID
					  , CNTN
					  , HTML_CNTN
					  , REG_DT )
VALUES (  #{qaNotiwrNo}
	  	, #{ttl}
	  	, #{id}
	  	, #{cntn}
	  	, #{htmlCntn}
	  	, TO_CHAR(sysdate, 'YYYY-MM-DD HH24:MI:SS'))

mapper.select:
SELECT COUNT(*)
  FROM EMPL_RECO
 WHERE QA_NOTIWR_NO = #{qaNotiwrNo} 
   AND id = #{id}

mapper.update:
UPDATE EMPL_QST
   SET RECO_CNT = TO_NUMBER(RECO_CNT) + 1
 WHERE QA_NOTIWR_NO = #{qaNotiwrNo}

UPDATE EMPL_QST


		 ttl = #{ttl},


		 cntn = #{cntn},


		 HTML_CNTN = #{htmlCntn}


WHERE QA_NOTIWR_NO = #{qaNotiwrNo}

UPDATE EMPL_QST
   SET INQ_CNT = TO_NUMBER(INQ_CNT) + 1
 WHERE QA_NOTIWR_NO = #{qaNotiwrNo}

