 WHERE RPLY_NO = #{rplyNo}

mapper.insert:

 	SELECT 'ITV'|| INTRV_SCRN_SEQ.NEXTVAL as intrvNo
	FROM DUAL

INSERT INTO INTRV_SCRN(	  intrv_no
						, co_nm
						, duty
						, carr
						, appr
						, dif
						, intrv_typ
						, intrv_rcnt
						, spc_ctnt
						, pass_yn
						, id
						, intrv_img
						, qst_cntn
						, intrv_dt
						, reg_dt)
VALUES (  #{intrvNo}
		, #{coNm}
		, #{duty}
		, #{carr}
		, #{appr}
		, #{dif}
		, #{intrvTyp}
		, #{intrvRcnt}
		, #{spcCtnt}
		, #{passYn}
		, #{id}
		, #{intrvImg}
		, #{qstCntn}
		, #{intrvDt}
		, TO_CHAR(sysdate, 'YYYY-MM-DD HH24:MI:SS'))

mapper.select:
SELECT INTRV_NO , ID , CO_NM , get_common(DUTY) as duty , get_common(CARR) as carr , get_common(APPR) as appr , get_common(DIF) as dif , get_common(INTRV_TYP) as intrvTyp , get_common(INTRV_RCNT) as intrvRcnt , get_common(PASS_YN) as passYn , INTRV_IMG , QST_CNTN , SPC_CTNT , REG_DT , ACCP_YN , INTRV_DT FROM intrv_scrn WHERE ACCP_YN = 'Y'
AND duty = #{duty}
AND carr = #{carr}
AND UPPER(CO_NM) LIKE '%' || UPPER(#{coNm}) || '%'
ORDER BY REG_DT DESC, INTRV_DT DESC

