mapper.select:
SELECT * FROM (SELECT a.* , rownum rn FROM (SELECT R.TTL , K.SKILL , C.CO_NM , get_common(WKSITE) AS WKSITE , R.EXPR_DT , I.CO_IMG , R.RECRUIT_NO , (SELECT S.RECRUIT_NO FROM RECRUIT_SCRAP S WHERE R.RECRUIT_NO = S.RECRUIT_NO AND ID = #{id}) AS scrapYn , F.ID_CO AS followYn , R.ACCP_DT FROM RECRUIT_ANUN R JOIN CO C ON R.ID = C.ID JOIN (SELECT RECRUIT_NO , LISTAGG(get_common(SKILL), ',') WITHIN GROUP (ORDER BY SKILL_NO) as SKILL FROM RECRUIT_SKILL GROUP BY recruit_no) K ON R.RECRUIT_NO = K.RECRUIT_NO LEFT JOIN (SELECT ID_CO FROM FOLLOWING WHERE ID_MEM = #{id}) F ON R.ID = F.ID_CO JOIN (SELECT DISTINCT RECRUIT_NO, CO_IMG FROM RECRUIT_IMAGES) I ON R.RECRUIT_NO = I.RECRUIT_NO WHERE 1=1
AND EXISTS (SELECT S.RECRUIT_NO FROM RECRUIT_SCRAP S WHERE R.RECRUIT_NO = S.RECRUIT_NO AND ID = #{id})
AND R.JOB = #{job}
AND R.WKSITE = #{wksite}
AND R.CARR = #{carr}
AND K.SKILL LIKE '%' || get_common(#{skill}) || '%'
AND TRUNC(sysdate) < TO_DATE(EXPR_DT, 'YYYY-MM-DD') AND R.ACCP_DT IS NOT NULL ORDER BY RECRUIT_NO DESC ) a ) WHERE rn > (#{pageNum}-1) * 8 AND rn <= #{pageNum} * 8

SELECT COUNT(*) FROM RECRUIT_ANUN R JOIN CO C ON R.ID = C.ID JOIN (SELECT RECRUIT_NO , LISTAGG(get_common(SKILL), ',') WITHIN GROUP (ORDER BY SKILL_NO) as SKILL FROM RECRUIT_SKILL GROUP BY recruit_no) K ON R.RECRUIT_NO = K.RECRUIT_NO LEFT JOIN (SELECT ID_CO FROM FOLLOWING WHERE ID_MEM = #{id}) F ON R.ID = F.ID_CO JOIN (SELECT DISTINCT RECRUIT_NO, CO_IMG FROM RECRUIT_IMAGES) I ON R.RECRUIT_NO = I.RECRUIT_NO WHERE 1=1
AND EXISTS (SELECT S.RECRUIT_NO FROM RECRUIT_SCRAP S WHERE R.RECRUIT_NO = S.RECRUIT_NO AND ID = #{id})
AND R.JOB = #{job}
AND R.WKSITE = #{wksite}
AND R.CARR = #{carr}
AND K.SKILL LIKE '%' || get_common(#{skill}) || '%'
AND TRUNC(sysdate) < TO_DATE(EXPR_DT, 'YYYY-MM-DD') AND R.ACCP_DT IS NOT NULL ORDER BY RECRUIT_NO DESC

SELECT R.TTL, K.SKILL, C.CO_NM, get_common(WKSITE) AS WKSITE, R.EXPR_DT, I.RECRUIT_IMG, R.RECRUIT_NO, (SELECT S.RECRUIT_NO FROM RECRUIT_SCRAP S WHERE R.RECRUIT_NO = S.RECRUIT_NO AND ID = #{id}) AS scrapYn, (SELECT F.ID_CO FROM FOLLOWING F WHERE R.ID = F.ID_CO AND F.ID_MEM = #{id}) AS followYn, get_common(JOB) AS JOB, R.MA_BUSS, R.QUAL_COND, R.PREF_COND, R.WELF, get_common(CARR) AS CARR, get_common(SHCR) AS SHCR, R.CO_INTRO, R.RECRUIT_NO, C.ID AS ID_CO FROM RECRUIT_ANUN R JOIN CO C ON R.ID = C.ID JOIN ( SELECT RECRUIT_NO, LISTAGG(get_common(SKILL), ',') WITHIN GROUP (ORDER BY SKILL_NO) AS SKILL FROM RECRUIT_SKILL GROUP BY RECRUIT_NO ) K ON R.RECRUIT_NO = K.RECRUIT_NO JOIN ( SELECT RECRUIT_NO, LISTAGG(RECRUIT_IMG, ',') WITHIN GROUP (ORDER BY RECRUIT_IMG_NO) AS RECRUIT_IMG FROM RECRUIT_IMAGES GROUP BY RECRUIT_NO ) I ON R.RECRUIT_NO = I.RECRUIT_NO WHERE R.RECRUIT_NO = #{recruitNo}
AND EXISTS (SELECT S.RECRUIT_NO FROM RECRUIT_SCRAP S WHERE R.RECRUIT_NO = S.RECRUIT_NO AND ID = #{id})
AND EXISTS (SELECT F.ID_CO FROM FOLLOWING F WHERE R.ID = F.ID_CO AND F.ID_MEM = #{id})

SELECT K.RECRUIT_NO, R.TTL, get_common(CARR) AS CARR, get_common(JOB) AS JOB, LISTAGG(get_common(SKILL), ',') WITHIN GROUP (ORDER BY SKILL_NO) as SKILL, R.ACCP_DT, C.CO_NM, I.CO_IMG FROM RECRUIT_SKILL K JOIN RECRUIT_ANUN R ON K.RECRUIT_NO = R.RECRUIT_NO JOIN CO C ON C.ID = R.ID JOIN RECRUIT_IMAGES I ON R.RECRUIT_NO = I.RECRUIT_NO JOIN (SELECT DISTINCT RECRUIT_NO, CO_IMG FROM RECRUIT_IMAGES) I ON R.RECRUIT_NO = I.RECRUIT_NO WHERE 1=1 AND get_common(SKILL) IN
#{skill}
GROUP BY K.RECRUIT_NO, R.TTL, CARR, JOB, R.ACCP_DT, C.CO_NM, I.CO_IMG ORDER BY K.RECRUIT_NO

SELECT CO_NM
FROM CO 
WHERE ID = #{id}
