mapper.update:
UPDATE member
   SET pw = #{pw}


		id = #{id}


	 	email = #{email}

mapper.select:
SELECT id
FROM member
WHERE name=#{name}
  AND ph_cl=#{phCl}

SELECT count(email)
FROM member
WHERE name=#{name}
  AND email=#{email} 
  AND ph_cl=#{phCl}
  AND api IS NULL

SELECT id
FROM member
WHERE bizno=#{bizno}
  AND rpstr=#{rpstr}

SELECT m.id
FROM member m, co c
WHERE m.id = c.id
  AND c.bizno=#{bizno}
  AND c.rpstr=#{rpstr}
