mapper.select:
select * from ( select rownum rn, a.* from( SELECT DISTINCT jsn.job_search_no , jsn.resume_no , jsn.id , jsn.posit , get_common(jsn.pubc_range) as pubcRange , jsn.ttl , get_common(jsn.hope_wksite) as hopeWksite , get_common(jsn.duty_fld) as dutyFld , jsn.hope_sal , jsn.myimg , jsn.interest , jsn.prop , jsn.reg_dt , LISTAGG(get_common(jss.skill), ',') WITHIN GROUP (ORDER BY jss.skill_no) AS skill
FROM job_search_notiwr jsn JOIN job_search_skill jss ON jsn.job_search_no = jss.job_search_no
(jsn.id = #{id} OR jsn.pubc_range = 'c3')
(jsn.pubc_range = 'c3' OR jsn.pubc_range = 'c2')
AND jsn.duty_fld = #{dutyFld}
AND jsn.career = #{career}
AND jsn.hope_wksite = #{hopeWksite}
GROUP BY jsn.job_search_no, jsn.resume_no, jsn.id, jsn.posit, jsn.pubc_range, jsn.ttl, jsn.hope_wksite, jsn.duty_fld, jsn.hope_sal, jsn.myimg, jsn.interest, jsn.prop, jsn.reg_dt
HAVING UPPER(LISTAGG(get_common(jss.skill), ',') WITHIN GROUP (ORDER BY jss.skill_no)) LIKE '%' || UPPER(#{skill}) || '%'
ORDER BY jsn.job_search_no DESC ) a where rownum <= #{pageNum}*#{showPage} ) b where rn > (#{pageNum}-1)*#{showPage}

SELECT count(*) from( SELECT DISTINCT jsn.job_search_no , jsn.resume_no , jsn.id , jsn.posit , get_common(jsn.pubc_range) as pubcRange , jsn.ttl , get_common(jsn.hope_wksite) as hopeWksite , get_common(jsn.duty_fld) as dutyFld , jsn.hope_sal , jsn.myimg , jsn.interest , jsn.prop , jsn.reg_dt , LISTAGG(get_common(jss.skill), ',') WITHIN GROUP (ORDER BY jss.skill_no) AS skill
FROM job_search_notiwr jsn JOIN job_search_skill jss ON jsn.job_search_no = jss.job_search_no
(jsn.id = #{id} OR jsn.pubc_range = 'c3')
(jsn.pubc_range = 'c3' OR jsn.pubc_range = 'c2')
AND jsn.duty_fld = #{dutyFld}
AND jsn.career = #{career}
AND jsn.hope_wksite = #{hopeWksite}
GROUP BY jsn.job_search_no, jsn.resume_no, jsn.id, jsn.posit, jsn.pubc_range, jsn.ttl, jsn.hope_wksite, jsn.duty_fld, jsn.hope_sal, jsn.myimg, jsn.interest, jsn.prop, jsn.reg_dt
HAVING UPPER(LISTAGG(get_common(jss.skill), ',') WITHIN GROUP (ORDER BY jss.skill_no)) LIKE '%' || UPPER(#{skill}) || '%'
ORDER BY jsn.job_search_no DESC)

SELECT JOB_SEARCH_NO, id
  FROM inter_job_search
 WHERE id = #{id}

SELECT job_search_no, id
  FROM job_search_st
 WHERE id = #{id}

SELECT count(resume_no) 
FROM resume
WHERE id = #{id}

SELECT DISTINCT jsn.job_search_no, jsn.resume_no, jsn.id, jsn.ttl, get_common(jsn.hope_wksite) as hopeWksite, jsn.posit, get_common(jsn.duty_fld) as dutyFld, jsn.pubc_range, jsn.intro, jsn.hope_sal, jsn.residency, jsn.myimg, jsn.interest, jsn.prop, jsn.reg_dt, get_common(jsn.career) as career, m.name, m.email, LISTAGG(get_common(jss.skill), ',') WITHIN GROUP(ORDER BY jss.skill_no) OVER(PARTITION BY jsn.job_search_no) AS skill
FROM job_search_notiwr jsn JOIN member m ON jsn.id = m.id JOIN job_search_skill jss ON jsn.job_search_no = jss.job_search_no
WHERE jsn.job_search_no = #{jobSearchNo}

select carr_no, shcr_no 
from resume
where resume_no = #{resumeNo}
