mapper.update:
BEGIN
	UPDATE prcs_prog
	SET infer_cnt = #{inferCnt},
		prcs_chk_cd = #{prcsChkCd},
		prcs_status = #{prcsNm}||'검사완료'
	WHERE indica_cd = #{indicaCd} AND prcs_cd = #{prcsCd};

	INSERT INTO prcs_prog_infer (
						    	  prcs_chk_cd,
								  prcs_cd,
								  indica_cd,
								  infer_cnt
								)
						VALUES (
								  #{prcsChkCd},
								  #{prcsCd},
								  #{indicaCd},
								  #{inferCnt}
								);		
END;
