mapper.insert:

	        SELECT PRO_SEQ_PLANCD.NEXTVAL FROM DUAL

  		DECLARE
			v_prdt_nm	VARCHAR2(100);
			v_pran_cd	VARCHAR2(100);
  		BEGIN
  			v_prdt_nm := #{prdtNm};
  			v_pran_cd := 'PLN' || ${planCd};

  			SELECT 	prdt_nm
  			INTO	v_prdt_nm
  			FROM 	edcts
  			WHERE	edcts_cd = #{edctsCd};



  			INSERT INTO plan (plan_cd,
  							  plan_name,
  							  paprd_dt,
  							  now_st,
  							  plan_dt

  							  )
  					VALUES	 (v_pran_cd,
  							  #{planName},
  							  #{paprdDt},
  							  '미지시',
  							  sysdate

  							  );

  			INSERT INTO plan_dtl (plan_cd,
	  							  pref_rank,
	  							  edcts_cd,
	  							  bom_cd,
	  							  order_cnt,
	  							  wk_to_dt,
	  							  prdt_nm
	  							  )
	  					VALUES	 (v_pran_cd,
	  							  #{prefRank},
	  							  #{edctsCd},
	  							  #{bomCd},
	  							  #{orderCnt},
	  							  #{wkToDt},
	  							  v_prdt_nm
	  							  );


	  		UPDATE 	order_sheet_test
	  		SET	 	prog_appe = '계획완료'
	  		WHERE	order_no = #{orderNo};

  		END;

DECLARE v_prdt_nm VARCHAR2(100); v_plan_cd VARCHAR2(100); BEGIN v_prdt_nm := #{prdtNm};
SELECT prdt_nm INTO v_prdt_nm FROM edcts WHERE edcts_cd = #{edctsCd};
SELECT plan_cd INTO v_plan_cd FROM ( SELECT plan_cd FROM plan ORDER BY plan_dt DESC ) WHERE ROWNUM = 1;
INSERT INTO plan_dtl (plan_cd, pref_rank, edcts_cd, bom_cd, order_cnt, wk_to_dt, prdt_nm ) VALUES (v_plan_cd, #{prefRank}, #{edctsCd}, #{bomCd}, #{orderCnt}, #{wkToDt}, v_prdt_nm ); END;

mapper.select:
SELECT bom_cd, standard, edcts_cd FROM bom
WHERE edcts_cd = #{edctsCd}
ORDER BY bom_cd

mapper.update:
UPDATE 	plan
SET	 	now_st = '계획취소'
WHERE	plan_cd = #{planCd}

DECLARE
	v_edcts_cd	VARCHAR2(100);
BEGIN
	v_edcts_cd := #{edctsCd};

	SELECT 	edcts_cd
	INTO 	v_edcts_cd
	FROM 	edcts
	WHERE 	prdt_nm = #{prdtNm};

	UPDATE 	plan
	SET 	plan_dt = sysdate,
			plan_name = #{planName}
	WHERE 	plan_cd = #{planCd};

	UPDATE 	plan_dtl
	SET 	order_cnt = #{orderCnt},
			wk_to_dt = #{wkToDt},
			prdt_nm = #{prdtNm},
			edcts_cd = v_edcts_cd
	WHERE 	plan_cd = #{planCd};
END;
